import React, { useState, useEffect } from 'react'
import CartStyle from './css/Cart.module.css'
import CartPageStyle from './css/CartPage.module.css'
import Logo from "./Logo"
import SearchBar from "./SearchBar"
import Nav from "./Nav"
import CartButton from "./CartButton"

function Cart() {

    const [products, setProducts] = useState([])
    console.log("state", products)

    //fetches data, converts to json and resets the state
    const fetchProducts = () => {
        fetch('http://localhost:3000/cart/')
        .then(response => response.json())
        .then(data => {
            setProducts(data)
            //console.log('cart-fetch',data)

          })
        .catch(firstGetError => console.log(firstGetError))
      }
  // Waits for components to mount then calls fetch
      useEffect(fetchProducts, [])

    // function deleteProducts(id) {
    //     if(window.confirm("Are you sure you want to remove this item?")){
    //         fetch( 'http://localhost:3000/cart'+id , {
    //             method: 'DELETE',
    //             // headers: {
    //             //   'Accept': 'application/json', 
    //             //   'Content-Type': 'application/json'
    //             // },
    //             body: JSON.stringify("")
    //         })
    //     }
    //   fetchProducts()
    // }

    async function emptyCart() {
        try {
          const res = await fetch("http://localhost:3000/cart", {
            method: "DELETE",
          });
          await res.json();
          fetchProducts();
          products.history.push("/");
        } catch (err) {
          console.log(err);
        }
      }



    return(
        <div className={CartPageStyle.catcontainer} key={98798798790}>
        <Logo className={CartPageStyle.logo}/>
        <SearchBar className={CartPageStyle.search}/>
        <CartButton className={CartPageStyle.cartlogo}/>
        <Nav />
            <div className={CartStyle.container}>

                <ul className={CartStyle.list}>
                    {products.map(prod => (
                        <li className={CartStyle.item}>           
                            <p className={CartStyle.title}>{prod.title}</p>
                            <p className={CartStyle.price}>${prod.price}</p>
                            <a className={CartStyle.removeContainer}>
                                <button onClick={(e) => emptyCart()} className={CartStyle.remove}>X</button>
                            </a>
                        </li>
                    ))}
                </ul>    
            </div>
        </div>
    )
}
export default Cart
