                <p>{id}</p>
              </div>
            </div>
            <div className="buy">
              {isInCart && <button className="in-cart" >Added to shopping cart</button>}
              {!isInCart && <button className="notincart" onClick={this.handleAdoptPet} >Add to shopping cart</button>}
            </div>
          </div>
        )
    }
}

export default ProductCard
