import React, {useState} from 'react'
import SearchStyle from './SearchBar.module.css'


const SearchBar = ({products}) => {
    const newProducts = products.map(prod => prod.title)

    const [searchValue, setSearchValue] = useState("")

    const handleInputChange = (event) => {
        setSearchValue(event.target.value)
    }

    const handleClearCLick = () => {
        setSearchValue("")
    }

    const shouldDisplayButton = searchValue.length > 0

    const filteredProducts = newProducts.filter((product) => {
        return product.includes(searchValue)
    })

    return (
    <div className="wrapper">
        <input type="search" className={SearchStyle.searchbar} placeholder="Search" value={searchValue} onChange={handleInputChange} />
        {/* {shouldDisplayButton && <button onClick={handleClearCLick}>clear</button>} */}

        {/* <ul>
            {filteredProducts.map((product) => {
                return <li key={product}>{product}</li>
            })}
        </ul> */}


    </div>
    )
}

export default SearchBar
