import React, { useState, useEffect } from 'react'
import CategoryStyle from './css/category.module.css'
import CartStyle from './css/Cart.module.css'
import Logo from "./Logo"
import SearchBar from "./SearchBar"
import Nav from "./Nav"
import CartButton from "./CartButton"

function Cart() {

    const [products, setProducts] = useState([])
    console.log("state", products)
    //fetches data, converts to json and resets the state
    const fetchProducts = () => {
        fetch('http://localhost:3000/cart/')
        .then(response => response.json())
        .then(data => {
            setProducts(data)
            //console.log('cart-fetch',data)

          })
        .catch(firstGetError => console.log(firstGetError))
      }
  // Waits for components to mount then calls fetch
      useEffect(fetchProducts, [])

    //   function filterProduct() {
    //     const selectedProduct = products.map((item) => 
    //         return selectedProduct)

    //     }

    return(
        <div className={CategoryStyle.catcontainer} key={98798798790}>
        <Logo />
        <SearchBar className={CategoryStyle.search}/>
        <CartButton className={CategoryStyle.cart}/>
        <Nav />
        <div>
            {products.map(prod => (
                        <div>
                            <p>
                                <a className={CartStyle.title}>{prod.title}</a>
                                <a className={CartStyle.price}>{prod.price}</a>
                                <a className={CartStyle.price}>{prod.length}</a>
                                <a className={CartStyle.price}>Remove</a>
                            </p>
                        </div>
                        ))}    
            </div>
        </div>
    )

}
export default Cart
