import React, {useContext, useEffect, useState} from 'react'
import {ProductsContext} from "../Contexts/ProductContext"
import { Link } from 'react-router-dom'
import CategoryStyle from './css/category.module.css'
import bluff from "./Images/bluff.png"

function MenClothing() {

        const [data, setData] = useState("")
        const  product  = useContext(ProductsContext)

        useEffect(() => {
            //console.log(product)
            const selectedProduct = product.products.filter((item) => item.category === 'men clothing');
            console.log(selectedProduct)
            setData(selectedProduct);

            console.log('dates', data);
            //console.log("category", productCategory);
            //console.log("selected product:", selectedProduct);
          }, []);


        return ( 
            <div className={CategoryStyle.catcontainer}>
                <img className={CategoryStyle.logo} src={bluff} alt="logo"/>
                <Link className={CategoryStyle.back} to="/">Back</Link>
                 <div>
                    {data.map(prod => (
                        <div key={prod.id} className={CategoryStyle.item}>

                            <img className={CategoryStyle.image} src={prod.image} alt={prod.title}/>
                            <p className={CategoryStyle.title}>{prod.title}</p>
                            <p className={CategoryStyle.price}> Price: ${prod.price}</p>

                        </div>
                        ))}

                </div>
            </div>

            )

}

export default MenClothing
