import React from 'react'

class ProductCard extends React.Component {



    render() {
        console.log(this.props.products)
        const productsArray = this.products.map(prod => (
            <div>
                <div  key={products.id}>
                <a className="title">
                    {this.state.products.title}
                </a>
                <img className="image">{this.state.products.image}</img>
                <div className="description">
                    <p>Price: {this.state.products.price}</p>
                    <p>{this.state.products.category}</p>
                    <p>{this.state.products.description}</p>
                    <p>{this.state.products.id}</p>
                </div>
                </div>
                <div className="buy">
                {isInCart && <button className="in-cart" >Added to shopping cart</button>}
                {!isInCart && <button className="notincart" onClick={this.handleAdoptPet} >Add to shopping cart</button>}
                </div>
            </div>
        ))
        return (
          <div className="card">
            {productsArray}
          </div>
        )
    }
}

export default ProductCard
