import React, {useEffect, useState} from 'react'
import ProductPageStyle from './css/ProductPage.module.css'
import Logo from "./Logo"
import SearchBar from "./SearchBar"
import Nav from "./Nav"
//import { useLocation } from "react-router-dom";

function ProductPage(props) {

    const page_products = 'products'
    const page_cart = 'cart'

    const [cart, setCart] = useState([]);
    const [page, setPage] = useState(page_products)

    const addToCart = (props) => {
        console.log("we in cart")
        setCart([...cart, props])
    }

    const navigateTo = (nextPage) => {
        setPage(nextPage)
    }

    //const location = useLocation();

    // useEffect(() => {
    //     console.log(location.pathname); // result: '/secondpage'
    //     console.log(location.search); // result: '?query=abc'
    //     console.log(location.state.detail); // result: 'some_value'
    //  }, [location]);

    const renderProducts = () => (
        <div className={ProductPageStyle.prodcontainer} key={props.location.prod.prod.id}>
                    <p className={ProductPageStyle.title}>{props.location.prod.prod.title}</p>
                    <img className={ProductPageStyle.image} src={props.location.prod.prod.image} alt={props.location.prod.prod.title}/>
                        <div >
                            <p className={ProductPageStyle.price}> Price: ${props.location.prod.prod.price}</p>
                            <button 
                                className={ProductPageStyle.addCart}
                                onClick={() => addToCart(props)}
                                > Add to Shopping Cart </button>
                            <p className={ProductPageStyle.description}>{props.location.prod.prod.description}</p>
                        </div>
                </div>
    )

    const renderCart = () => (
        <div className={ProductPageStyle.prodcontainer} key={props.location.prod.prod.id}>
                    <p className={ProductPageStyle.title}>{props.location.prod.prod.title}</p>
                    <img className={ProductPageStyle.image} src={props.location.prod.prod.image} alt={props.location.prod.prod.title}/>
                        <div >
                            <p className={ProductPageStyle.price}> Price: ${props.location.prod.prod.price}</p>
                            <button 
                                className={ProductPageStyle.Back}
                                onClick={() => navigateTo(page_products)}
                                > Back to Search Results </button>
                            <p className={ProductPageStyle.description}>{props.location.prod.prod.description}</p>
                        </div>
                </div>
    )

        // renders product images and information to the DOM
        return (
        <div className={ProductPageStyle.container} key={props.location.prod.prod.id}>
            <Logo />
            <SearchBar />
            <Nav />
            <div >
                <button onClick={() => navigateTo(page_cart)}>Cart ({cart.length})</button>
                {page === page_products && renderProducts()}
                {page === page_cart && renderCart()}


                {/* <div className="buy">
                {isInCart && <button className="in-cart" >Added to shopping cart</button>}
                {!isInCart && <button className="notincart" onClick={this.handleAddCart} >Add to shopping cart</button>}
                </div> */}
            </div>
        </div>
        )
    }   


export default ProductPage
